import React, {Component} from 'react';
import { List, Avatar, Button, Checkbox, Spin } from 'antd';
import satellite from "../assets/images/satellite.svg";


class SatelliteList extends Component {
    // selected stars are internal data
    // external data can only be read-only
    constructor(){
        super();
        this.state = {
            selected: []
        };
    }

    // e是一个事件对象 包含了一些事件的方法和对象
    onChange = e => {
        // step1: get current selected bucket
        const { dataInfo, checked } = e.target;
        const { selected } = this.state;

        // step2: add or remove current select sat
        const list = this.addOrRemove(dataInfo, checked, selected);

        // step3: update selected bucket
        this.setState({ selected: list })
    }

    // status means if this item is checked
    addOrRemove = (item, status, list) => {
        // to find if there is an item that matches the satid
        const found = list.some( entry => entry.satid === item.satid);
        // case 1: checked
        //      -> item not in the list : add to the list
        //      -> item in the list: do nothing
        // case 2: unchecked
        //      -> item in the list: remove item from the list
        //      -> item not in the list: do nothing
        if(status && !found){
            list=[...list, item]
        }

        if(!status && found){
            list = list.filter( entry => {
                return entry.satid !== item.satid;
            });
        }
        return list;
    }

    onShowSatMap = () =>{
        this.props.onShowMap(this.state.selected);
    }

    render() {
        const { selected } = this.state;
        const satList = this.props.satInfo ? this.props.satInfo.above : [];
        const { isLoad } = this.props;

        return (
            <div className="sat-list-box">
                <Button className="sat-list-btn"
                        size="large"
                        disabled={ selected.length === 0}
                        onClick={this.onShowSatMap}
                        >Track on the map</Button>
                <hr/>

                {
                    isLoad ?
                        <div className="spin-box">
                            <Spin tip="Loading..." size="large" />
                        </div>
                        :
                        <List
                            className="sat-list"
                            itemLayout="horizontal"
                            size="small"
                            dataSource={satList}
                            renderItem={item => (
                                <List.Item
                                    actions={[<Checkbox dataInfo={item} onChange={this.onChange}/>]}
                                >
                                    <List.Item.Meta
                                        avatar={<Avatar size={50} src={satellite} />}
                                        title={<p>{item.satname}</p>}
                                        description={`Launch Date: ${item.launchDate}`}
                                    />

                                </List.Item>
                            )}
                        />
                }
            </div>
        );

    }
}

export default SatelliteList;
